let data = [
    {
      id: 1,
      imageUrl: "https://elements-cover-images-0.imgix.net/9752043e-96b9-4de2-b4da-4be01ced9095?auto=compress%2Cformat&fit=max&w=1170&s=36f894b7f3d8b34626c894660cd33a57",
      title: "red wine",
   
    },
    {
      id: 2,
      imageUrl: "data:image/jpeg;base64,",
      title: "rose wine",
    
    },
    {
      id: 3,
      imageUrl:
        "https://images.squarespace-cdn.com/content/v1/573cde1422482ee54cac4bbb/1604943201527-KOGB8PBOIXPTJVIEMIDN/Str+bottle+by+itself.jpg?format=1000w",
      title: "white wine",
    
    },
    {
      id: 4,
      imageUrl: "https://cdn.shopify.com/s/files/1/1665/9229/products/30333_600x.jpg?v=1587668184",
      title: "red sweet wine",
    
    },
  ];
  
  const arrowLeft = document.getElementById("arrow-left");
  const arrowRight = document.getElementById("arrow-right");
  const sliderConetnt = document.getElementById("slider-content");
  const dotBurtuli = document.getElementsByClassName("dot");
  
  let sliderIndex = 0;
  
  function createAtag(item) {
    const tag = document.createElement("a");
    tag.setAttribute("href", item.url);
    tag.classList.add("slide");
  
    return tag;
  }
  
  function createImgtag(item) {
  const tagBGImage = document.createElement("div");
  tagBGImage.style.background = `url(${item.imageUrl})`;
  tagBGImage.classList.add("bg-image");
  
    return tagBGImage;
  }




  function createH2ag(item) {
    const tagTitle = document.createElement("h2");
    tagTitle.textContent = item.title;
    tagTitle.classList.add("slider-title");
  
    return tagTitle;
  }
  
  function createDots(item) {
    const dotsParent = document.createElement("div");
    dotsParent.classList.add("dotsParent");
  
    data.forEach((element) => {
      const dotChild = document.createElement("div");
      dotChild.classList.add("dot");
      dotChild.setAttribute("data-id", element.id - 1);
  
      dotChild.addEventListener("click", function (event) {
        let id = event.target.getAttribute("data-id");
        sliderIndex = id;
        setSlide();
      });
  
      dotsParent.appendChild(dotChild);
    });
  
    return dotsParent;
  }
  function cuurentDotactive() {
    dotBurtuli[sliderIndex].classList.add("active");
  }
  
  function setSlide() {
    sliderConetnt.innerHTML = "";
    const slideItem = createAtag(data[sliderIndex]);
    const h2Tag = createH2ag(data[sliderIndex]);
    const imgTag = createImgtag(data[sliderIndex]);
    const dots = createDots();
  
    slideItem.appendChild(imgTag);
    slideItem.appendChild(h2Tag);
    sliderConetnt.appendChild(slideItem);
    sliderConetnt.appendChild(dots);
  
    cuurentDotactive();
    console.log(slideItem);
  }
  
  function arrowLeftClick() {
    if (sliderIndex == 0) {
      sliderIndex = data.length - 1;
      setSlide();
      return;
    }
    sliderIndex--;
    
    setSlide();
  }
  function arrowRIghtClick() {
    if (sliderIndex == data.length - 1) {
      sliderIndex = 0;
      setSlide();
      return;
    }
    sliderIndex++;
    
    setSlide();
  }
  
  arrowLeft.addEventListener("click", arrowLeftClick);
  arrowRight.addEventListener("click", arrowRIghtClick);
  setInterval(() => {
    arrowRIghtClick();
  }, 5000);
  
  setSlide();
